mapper.update:
UPDATE t_user_wallet
SET balance = balance - #{amount},
    frozen_balance = frozen_balance + #{amount},
    version = version + 1,
    updated_at = NOW()
WHERE user_id = #{userId}
  AND balance >= #{amount}
  AND version = #{version}
  AND wallet_status = 1

UPDATE t_user_wallet
SET frozen_balance = frozen_balance - #{amount},
    version = version + 1,
    updated_at = NOW()
WHERE user_id = #{userId}
  AND frozen_balance >= #{amount}
  AND version = #{version}

UPDATE t_user_wallet
SET balance = balance - #{amount},
    version = version + 1,
    updated_at = NOW()
WHERE user_id = #{userId}
  AND balance >= #{amount}
  AND version = #{version}
  AND wallet_status = 1

UPDATE t_user_wallet
SET frozen_balance = frozen_balance - #{amount},
    balance = balance + #{amount},
    version = version + 1,
    updated_at = NOW()
WHERE user_id = #{userId}
  AND frozen_balance >= #{amount}
  AND version = #{version}
